mapper.select:
select *from sys_lab_info
del_flag=0
order by create_time desc limit #{p},#{m}

mapper.update:
update sys_lab_info
set valid_status = #{valid_status},updater=#{updater},update_time=NOW()

    del_flag=0 and lab_id=#{lab_id}

update sys_lab_info
set lab_status=#{lab_status},updater=#{updater},update_time=NOW()

    del_flag=0 and lab_id=#{lab_id}

update sys_lab_info set del_flag=1,del_time=NOW()
del_flag=0 and lab_id in
#{id}

mapper.select:
select * from sys_lab_info

    del_flag=0 and ${baseInfoKey} like concat('%',#{baseInfoValue},'%')

        and ${statusKey} = #{statusValue}


order by create_time desc
limit #{p},#{m}
