mapper.select:
SELECT DISTINCT sys_machine.*,img_url,is_tar,sort_id,sort_name from sys_machine,sys_machine_img,sys_machine_sort
sys_machine.img_code = sys_machine_img.img_code and sys_machine.class_code=sys_machine_sort.sort_id and sys_machine.del_flag=0 and sys_machine_img.del_flag=0 and sys_machine_sort.del_flag=0
order by sys_machine.create_time desc limit #{page},#{limit}

select *
from sys_machine_sort

    locate(#{key},${value}) and del_flag=0 limit #{page},#{limit}

mapper.update:
update sys_machine set
is_check=#{is_check},updater=#{updater},update_time=NOW()

    del_flag=0 and id=#{id}

mapper.select:
SELECT DISTINCT sys_machine.*,img_url,is_tar,sort_id,sort_name from sys_machine,sys_machine_img,sys_machine_sort
sys_machine.img_code = sys_machine_img.img_code and sys_machine.class_code=sys_machine_sort.sort_id and sys_machine.del_flag=0 and sys_machine_img.del_flag=0 and sys_machine_sort.del_flag=0 and ${baseInfoKey} like concat('%',#{baseInfoValue},'%')
and ${statusKey} = #{statusValue}
order by sys_machine.create_time desc limit #{p},#{m}

SELECT DISTINCT COUNT(*) from sys_machine,sys_machine_img,sys_machine_sort
sys_machine.img_code = sys_machine_img.img_code and sys_machine.class_code=sys_machine_sort.sort_id and sys_machine.del_flag=0 and sys_machine_img.del_flag=0 and sys_machine_sort.del_flag=0 and ${baseInfoKey} like concat('%',#{baseInfoValue},'%')
and ${statusKey} = #{statusValue}

mapper.update:
update sys_machine set del_flag=1,del_time=NOW()
del_flag=0 and id in
#{id}
