set real_name=#{real_name}, username=#{user_name}, user_phone=#{user_phone}, sex=#{sex}, role_code=#{role_code}, email=#{email},updater=#{updater},update_time=NOW()

    user_id=#{user_id}

update sys_user set del_flag=1,del_time=NOW()
user_id in
#{id}

mapper.select:
select sys_user.id,user_id,sys_user.role_code,role_name,real_name,username, user_phone,sex,email,is_disable,sys_user.creater,sys_user.create_time, sys_user.updater,sys_user.update_time from sys_user,sys_role
sys_user.role_code = sys_role.role_code and sys_user.del_flag=0 and ${searchKey} like concat('%',#{searchValue},'%')
order by sys_user.create_time desc limit #{p},#{m}

mapper.update:
update sys_user
set is_disable=#{is_disable},updater=#{updater},update_time=NOW()
where user_id = #{u_update_id} and del_flag=0;

update sys_user
set password=#{initPassword},updater=#{updater},update_time=NOW()
where user_id=#{u_update_id} and del_flag=0;
